@prefix iadopt: <https://w3id.org/iadopt/ont/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <http://vocabs.lter-europe.net/EnvThes/30333> ;
            sh:resultMessage "Variable needs to have exactly one Property." ;
            sh:resultPath iadopt:hasProperty ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:maxCount 1 ;
                    sh:message "Variable needs to have exactly one Property." ;
                    sh:minCount 1 ;
                    sh:nodeKind sh:IRI ;
                    sh:path iadopt:hasProperty ] ],
        [ a sh:ValidationResult ;
            sh:focusNode <http://vocabs.lter-europe.net/EnvThes/30333> ;
            sh:resultMessage "Variable needs to have exactly one ObjectOfInterest." ;
            sh:resultPath iadopt:hasObjectOfInterest ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:maxCount 1 ;
                    sh:message "Variable needs to have exactly one ObjectOfInterest." ;
                    sh:minCount 1 ;
                    sh:nodeKind sh:IRI ;
                    sh:path iadopt:hasObjectOfInterest ] ] .

